export function translate(key, lang = "fr") {
  if (!T[key]) return key;
  return T[key][lang] || T[key]["fr"] || key;
}

export function useT(lang = "fr") {
  return (key, fallback = "") => T[key]?.[lang] || T[key]?.["fr"] || fallback || key;
}
